import { createSlice } from "@reduxjs/toolkit";

const cartSlice=createSlice({
    name:"cart",
    initialState:{
        items:[],
        totalQuantity:0,
        totalAmount:0
    },
    reducers:{
        addItemToCart(state,action){
            const item=action.payload;
            const existingItems=state.items.find((event)=>event.payload===item);
            if(existingItems){
                existingItems.quantity++;
                existingItems.totalPrice=existingItems.totalPrice+item.price;
            }else{
                state.items.push({itemId:item.id,price:item.price,quantity:1,totalPrice:item.price,name:item.name});
            }
         
        },
        removeItemToCart(state,action){
            const item=action.payload;
            const existingItems=state.items.find((event)=>event.payload===item);
            if(existingItems.quantity>1){
                existingItems.quantity--;
                existingItems.totalPrice=existingItems.totalPrice-item.price;
            }else{
                state.items.filter((event)=>event.id!==item.id);

            }
        },

    }
})
export const dispatchCartSlice=cartSlice.actions.reducer;

export default cartSlice;
