// Challenge / Exercise

// 1. Add five new (dummy) page components (content can be simple <h1> elements)
//    - HomePage
//    - EventsPage
//    - EventDetailPage
//    - NewEventPage
//    - EditEventPage
// 2. Add routing & route definitions for these five pages
//    - / => HomePage
//    - /events => EventsPage
//    - /events/<some-id> => EventDetailPage
//    - /events/new => NewEventPage
//    - /events/<some-id>/edit => EditEventPage
// 3. Add a root layout that adds the <MainNavigation> component above all page components
// 4. Add properly working links to the MainNavigation
// 5. Ensure that the links in MainNavigation receive an "active" class when active
// 6. Output a list of dummy events to the EventsPage
//    Every list item should include a link to the respective EventDetailPage
// 7. Output the ID of the selected event on the EventDetailPage
// BONUS: Add another (nested) layout route that adds the <EventNavigation> component above all /events... page components

import { RouterProvider, createBrowserRouter} from 'react-router-dom'
import HomePage from './pages/HomePage';
import EventsPage, { loader as eventsLoader } from './pages/EventsPage';
import EditDetailPage, { eventItemLoader,action as actionDelete } from './pages/EventDetailPage';
import NewEventPage from './pages/NewEventPage';
import EditEventPage from './pages/EditEventPage';
import RootLayout from './pages/root/RootLayout';
import EventsNavigation from './components/EventsNavigation';
import Error from "./pages/Error";
import { action } from "./components/EventForm";
import NewsletterPage, { action as newsletterAction } from './pages/Newsletter';
function App() {
  const router=createBrowserRouter([
    {path:"/",element:<RootLayout/>,errorElement:<Error/>, children:[
      {index:true,element:<HomePage/>},
      {path:"events",element:<EventsNavigation/>,children:[
        {path:"",element:<EventsPage/>, loader:eventsLoader},
        {path:"new",element:<NewEventPage/>,action:action},
        {path:":id",id:"event-detail",loader:eventItemLoader,children:[
          {index:true,element:<EditDetailPage/>,action:actionDelete},
          {path:"edit",element:<EditEventPage/>,action:action}
        ]},
      ]},
      {
        path: 'newsletter',
        element: <NewsletterPage />,
        action: newsletterAction,
      },
    ]}
  ])

  return <RouterProvider router={router}/>;
}

export default App;
